export default function circleText(){document.querySelectorAll(".main-background__text_element").forEach((t,e)=>{t.innerHTML="";const n=document.createElement("canvas"),l=n.getContext("2d");t.append(n),n.width=1e3,n.height=1e3;const a=n.width/2,h=n.height/2,i="SPICE UP YOUR CAREER WITH US! ",o=Math.min(n.width,n.height)/2-60;l.font="700 142px Inter",l.fillStyle="white",l.textAlign="center",l.textBaseline="middle";const c=2*Math.PI/i.length;let r=-Math.PI/2;for(let t=0;t<i.length;t++){const e=i[t],n=a+o*Math.cos(r),d=h+o*Math.sin(r),s=r+Math.PI/2;l.save(),l.translate(n,d),l.rotate(s),l.fillText(e,0,0),l.restore(),r+=c}})}