export default function sliders(){document.querySelectorAll(".about-us__slider").forEach(e=>{const o=new Splide(e,{type:"fade",rewind:!0,arrows:!1,pagination:!1,autoplay:!0,interval:3e3,drag:!1,speed:1e3,easing:"ease",updateOnMove:!0,autoScroll:{speed:0},intersection:{inView:{autoplay:!0},outView:{autoplay:!1}}});o.on("mounted",()=>{setTimeout(()=>o.go(">"),3e3)}),document.querySelectorAll(".about-us__slide_media video").forEach(e=>{e.setAttribute("src",e.dataset.src),e.load(),e.addEventListener("loadeddata",()=>{e.play(),e.addEventListener("ended",()=>{e.currentTime=0,e.play()})})}),o.mount(window.splide.Extensions.Intersection)}),document.querySelectorAll(".news__slider").forEach(e=>{new Splide(e,{perPage:4,gap:"3.5rem",arrows:!1,pagination:!1,easing:"ease",speed:700,updateOnMove:!0,breakpoints:{992:{easing:"ease",speed:400,perPage:2,gap:"2rem",padding:{right:"1.25rem"}},550:{perPage:1,gap:"1rem"}}}).mount()}),document.querySelectorAll(".side-popup__decor_slider").forEach(e=>{new Splide(e,{type:"loop",gap:"1.5rem",autoWidth:!0,arrows:!1,pagination:!1,drag:!1,autoScroll:{speed:.8,pauseOnHover:!1,pauseOnFocus:!1}}).mount(window.splide.Extensions)}),document.querySelectorAll(".single-gallery").forEach(e=>{new Splide(e,{autoWidth:!0,arrows:!1,pagination:!1,gap:"1rem",padding:{left:"1rem",right:"1rem"},mediaQuery:"min",breakpoints:{992:{destroy:!0}}}).mount()}),document.querySelectorAll(".mentioned__slider").forEach(e=>{new Splide(e,{perPage:2,autoWidth:!0,arrows:!1,pagination:!1,focus:"center",gap:"4vw",mediaQuery:"min",breakpoints:{768:{destroy:!0},550:{}}}).mount()}),document.querySelectorAll(".marquee").forEach(e=>{new Splide(e,{type:"loop",gap:"1.5rem",autoWidth:!0,arrows:!1,pagination:!1,drag:!1,autoScroll:{speed:.8,pauseOnHover:!1,pauseOnFocus:!1},breakpoints:{992:{gap:"4vw"}}}).mount(window.splide.Extensions)})}