export default function sliders(){document.querySelectorAll(".about-us__slider").forEach(e=>{const t=new Splide(e,{type:"loop",direction:"ttb",perPage:1,height:"12.5rem",arrows:!1,pagination:!1,autoplay:!0,interval:3e3,drag:!1,speed:1e3,easing:"ease",updateOnMove:!0,autoScroll:{speed:0},intersection:{inView:{autoplay:!0},outView:{autoplay:!1}}});t.on("mounted",()=>{setTimeout(()=>t.go(">"),3e3)}),document.querySelectorAll(".about-us__slide_media video").forEach(e=>{e.setAttribute("src",e.dataset.src),e.load(),e.addEventListener("loadeddata",()=>{e.play(),e.addEventListener("ended",()=>{e.currentTime=0,e.play()})})}),t.mount(window.splide.Extensions.Intersection)}),document.querySelectorAll(".news__slider").forEach(e=>{new Splide(e,{perPage:4,gap:"3.5rem",arrows:!1,pagination:!1,easing:"ease",speed:700,updateOnMove:!0,breakpoints:{992:{easing:"cubic-bezier(0.25, 1, 0.5, 1)",speed:400,perPage:2,gap:"2rem"},550:{perPage:1,gap:"1rem"}}}).mount()})}